local UtilHelper = {}

local floor = math.floor
local random = math.random

function UtilHelper.can_spawn(chance_value, as_zero_one_range)
    if as_zero_one_range then
        chance_value = chance_value * 100
    end
    return random(1, 100) > (100 - chance_value)
end

function UtilHelper.from_chunk_position(pos)
    return { x = (floor(pos.x) * 32), y = (floor(pos.y) * 32)  }
end

function UtilHelper.to_chunk_position(pos)
    return { x = floor(pos.x / 32), y = floor(pos.y / 32) }
end

function UtilHelper.get_gps_message(x, y, surface_name)
    return "[gps=" .. x .. "," .. y .. "," .. surface_name .. "]"
end

return UtilHelper