local ignore_list = {
    --- Offical planets --
    nauvis = true,
    vulcanus = true,
    fulgora = true,
    gleba = true,
    aquilo = true,
    --- ERM Planets --
    char = true,
    aiur = true,
    earth = true,
    --- 3rd party planets with their defined uniqueness
    --- Maraxsis
    maraxsis = true,
    trench = true,
    naufulglebunusilo = true,
}

local is_not_ignore_planet = function(planet)
    if ignore_list[planet.name] and
       planet.hidden
    then
        return false
    end

    if planet.autoplace_control["enemy-base"] then
        return true
    end
    
    return false
end


--- {"planet-name":"enemy_autocontrol"}
local json_obj = {}
--- Enemy assignment only apply to planet without territory_settings unit
for _, planet in data.raw['planet'] do
    if is_not_ignore_planet(planet) then
        json_obj[planet.name] = 'enemy-base'
    end
end

settings.startup['enemyracemanager-enemy-planet-assignment'].default_value = helpers.table_to_json(json_obj)

